import React, { useState } from 'react';
import './Onglet.css';

export default function Onglet(){

    const [stateOnglets, setStateOnglets] = useState(1);
    const goFR = () => {
        setStateOnglets(1);
    }

    const goEN = () => {
        setStateOnglets(2);
    }

    return(
        <div className="divBody">
            <div className="contBtn">
                <div onClick={ goFR } className={`onglets ${stateOnglets === 1 ? 'active' : ''}`}>Accueil</div>
                <div onClick={ goEN } className={`onglets ${stateOnglets === 1 ? 'active' : ''}`}>Bois</div>
                <div className={`onglets ${stateOnglets === 1 ? 'active' : ''}`}>Couture</div>
                <div className="onglets">DIY</div>
                <div className="onglets">Geekeries</div>
            </div>
            <div className="container">
                {stateOnglets === 1 ?
                <p className="contenu fr">
                    FR Lorem ipsum dolor sit amet consectetur adipisicing elit. Aspernatur dolorum placeat, totam ad consectetur quia sit reprehenderit velit. Non laboriosam explicabo asperiores similique eligendi id unde nostrum adipisci, minima expedita?
                </p>
                :
                <p className="contenu en">
                    EN Aspernatur dolorum placeat, totam ad consectetur quia sit reprehenderit velit. Non laboriosam explicabo asperiores similique eligendi id unde nostrum adipisci, minima expedita?
                </p>
                }
            </div>
        </div>
    )
}
